'use strict';
const EtherealEmail = require('../lib/ethereal-email');

let inbox = new EtherealEmail();

inbox.on('email', email => {
    console.log('Listening for messages sent to <%s>', email);
});

inbox.connect(mail => {
    console.log('NEW MAIL!');
    console.log(mail);
});
